INSERT INTO `dw_article` VALUES ('94', '22', '商富贷林总致广大投资者的公开信 ', null, '1', null, null, '', '2013-01-04 16:18:08', null, '', null, '', null, null, null, '<p style=\"padding-bottom:0px;line-height:36px;background-color:#ffffff;margin-top:0px;text-indent:24pt;padding-left:0px;padding-right:0px;font-family:微软雅黑;white-space:normal;margin-bottom:0px;height:auto;padding-top:0px;\">\r\n	<span style=\"line-height:32px;font-size:16pt;\">2013</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">年</span><span style=\"line-height:32px;font-size:16pt;\">1</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">月</span><span style=\"line-height:32px;font-size:16pt;\">4</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">日，杭城内外银装素裹，分外妖娆；今天，商富公司线上模式</span><span style=\"line-height:32px;font-size:16pt;\">&shy;</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">——商富贷正式运营，在此我代表商富全体员工对广大客户及各界朋友表示衷心的感谢！</span><span style=\"line-height:32px;font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"padding-bottom:0px;line-height:36px;background-color:#ffffff;margin-top:0px;text-indent:24pt;padding-left:0px;padding-right:0px;font-family:微软雅黑;white-space:normal;margin-bottom:0px;height:auto;padding-top:0px;\">\r\n	<span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">此刻，我们万分惊喜，商富公司自</span><span style=\"line-height:32px;font-size:16pt;\">2011</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">年</span><span style=\"line-height:32px;font-size:16pt;\">09</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">月成立至今，得到了广大客户与社会各界朋友的支持和认可，至此商富公司线上模式——商富贷正式运营，又有这么多新老客户的关心和支持，本人深表感激；另一方面，我们战战兢兢，我们做的是互联网，一不小心就可能被唾液淹死，我们做的是金融，风险控制不好保证投资者的利益谈何容易；在此我们庄严承诺，一：我们不跑路；二：我们将竭尽全力把商富贷做成网贷行业的佼佼者。</span><span style=\"line-height:32px;font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"padding-bottom:0px;line-height:36px;background-color:#ffffff;margin-top:0px;text-indent:24pt;padding-left:0px;padding-right:0px;font-family:微软雅黑;white-space:normal;margin-bottom:0px;height:auto;padding-top:0px;\">\r\n	<span style=\"line-height:32px;font-size:16pt;\">2013</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">年我们将在以下方面开展工作：一加强技术创新，让我们这个平台更好用，不断提高网站的透明度，安全度；二加强业务推广，加强风险控制，推优质标，保证大家的资金安全，提高网站的公信力，知名度，美誉度；三加强公司内部管理，加强人才培养，加强资源整合，提高公司的正能量。四加强和投资者的沟通，接受大家的建议，保证投资者的利益。</span><span style=\"line-height:32px;font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"padding-bottom:0px;line-height:36px;background-color:#ffffff;margin-top:0px;text-indent:24pt;padding-left:0px;padding-right:0px;font-family:微软雅黑;white-space:normal;margin-bottom:0px;height:auto;padding-top:0px;\">\r\n	<span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">朋友们，新一年的到来，标志着商富公司线下运作已横跨三年，我们过去走过的道路沉淀着量的积累，我们未来的道路必将展示质的飞跃！</span><span style=\"line-height:32px;font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"padding-bottom:0px;line-height:36px;background-color:#ffffff;margin-top:0px;text-indent:24pt;padding-left:0px;padding-right:0px;font-family:微软雅黑;white-space:normal;margin-bottom:0px;height:auto;padding-top:0px;\">\r\n	<span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">杭州是人间天堂，是休闲之都，是品质之城，邀请朋友到杭州来旅游，到我们公司来考察！</span><span style=\"line-height:32px;font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"padding-bottom:0px;line-height:36px;background-color:#ffffff;margin-top:0px;text-indent:24pt;padding-left:0px;padding-right:0px;font-family:微软雅黑;white-space:normal;margin-bottom:0px;height:auto;padding-top:0px;\">\r\n	<span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">杭州欢迎您！商富欢迎您！顺祝大家新年快乐，身体健康，财源广进！</span><span style=\"line-height:32px;font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"padding-bottom:0px;line-height:36px;background-color:#ffffff;margin-top:0px;text-indent:168pt;padding-left:0px;padding-right:0px;font-family:微软雅黑;white-space:normal;margin-bottom:0px;height:auto;padding-top:0px;\">\r\n	<span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 林泽标</span><span style=\"line-height:32px;font-size:16pt;\">&nbsp;&nbsp;2013</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">年</span><span style=\"line-height:32px;font-size:16pt;\">1</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">月</span><span style=\"line-height:32px;font-size:16pt;\">4</span><span style=\"line-height:32px;font-family:宋体;font-size:16pt;\">日</span> \r\n</p>', '10', '0', '0', null, '1', '1357303044', '219.82.22.9');
