INSERT INTO `dw_article` VALUES ('105', '126', '商富贷公司简介', null, '1', null, null, '商富贷', '2013-01-10 10:36:42', null, '', null, '', null, null, null, '<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">商富贷<span>(www.sfdai.com)</span><span>是杭州商富商务信息咨询有限公司（以下简称</span><span>“</span><span>商富</span><span>”</span><span>）依托互联网推出的</span><span>“</span><span>在线</span><span>”</span><span>理财贷款服务平台。该平台创新性的将电子商务与金融服务相结合</span><span>,</span><span>为客户提供在线投资与借款服务，并致力于打造一个规范、安全、高效、诚信、专业，共赢的网络借贷中介服务平台。</span></span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">商富贷拥有一支精通金融理财、互联网技术和政策法规的专业队伍，并依托浙江大学，浙江金融学院知名金融专家<span>“</span><span>智囊库</span><span>”</span><span>，形成了安全、稳健、专业、诚信的经营风格。目前商富贷有&nbsp;</span><span>35</span><span>名员工，公司设立总经办，人事行政部，财务部，信贷部，风险控制部，理财服务部，客户服务部，技术服务部。</span></span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">商富贷将履行我们对投资者的庄严承诺，为投资者持续创造财富；商富贷将秉承谨慎授信的原则，寻找优质的借款者；<a href=\"http://www.sfdai.com\" target=\"_blank\">商富贷</a>将不断改进，不断完善，不断探索出具有自身特色的盈利模式，我们的目标是通过三年的努力，成为中国网贷行业的佼佼者。</span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">公司地址：杭州市建国北路<span>236</span><span>号诚信大厦</span><span>1006</span><span>室（建国北路和凤起路交叉口）</span></span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">　&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;财富热线：<span>4000-511113</span></span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">企业愿景：打造一个规范、安全、高效、诚信、专业，共赢的网络借贷中介服务平台。</span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">核心价值观：至诚，尚德，永续成长&nbsp;</span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">经营理念：商以德立，富则诚铸&nbsp;</span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\">服务宗旨：诚信天下，客户为先</span> \r\n</p>\r\n<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n<p>\r\n	<img style=\"width:651px;height:564px;\" title=\"商富贷\" alt=\"商富贷\" src=\"/data/upload/32020130110103922.jpg\" width=\"879\" height=\"660\" /> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<div>\r\n	<img title=\"商富贷网贷平台\" alt=\"商富贷网贷平台\" src=\"/data/upload/32020130110103946.jpg\" /> \r\n</div>\r\n<div>\r\n	<span style=\"font-size:14px;\"><a href=\"http://www.sfdai.com\" target=\"_blank\">商富贷</a>公司接待室</span> \r\n</div>\r\n<div>\r\n	<span style=\"font-size:14px;\"></span>&nbsp;\r\n</div>\r\n<div>\r\n	<img title=\"商富贷网贷平台\" alt=\"商富贷网贷平台\" src=\"/data/upload/32020130110104027.jpg\" /> \r\n</div>\r\n<p>\r\n	<span style=\"font-size:14px;\"><a href=\"http://www.sfdai.com\" target=\"_blank\">商富贷</a>公司办公环境</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"></span>&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"></span><img style=\"width:725px;height:720px;\" title=\"商富贷网贷平台\" alt=\"商富贷网贷平台\" src=\"/data/upload/32020130110104122.jpg\" width=\"685\" height=\"695\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<a href=\"http://www.sfdai.com\">商富贷</a>全体员工\r\n</p>', '10', '0', '0', null, '1', '1358993561', '115.197.208.216');
INSERT INTO `dw_article` VALUES ('106', '126', '商富贷林总继开业之际致广大投资者', null, '1', null, null, '商富贷', '2013-01-10 11:36:34', null, '', null, '', null, null, null, '<div>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:16pt;\"><span style=\"font-size:14px;\">2013</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\">1</span><span style=\"font-size:14px;\">月</span><span style=\"font-size:14px;\">4</span><span style=\"font-size:14px;\">日，杭城内外银装素裹，分外妖娆；今天，商富公司线上模式——商富贷正式运营，在此我代表商富全体员工对广大客户及各界朋友表示衷心的感谢！</span></span><span style=\"font-family:\'Calibri\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:16pt;\"><span style=\"font-size:14px;\">此刻，我们万分惊喜，商富公司自</span><span style=\"font-size:14px;\">2011</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\">09</span><span style=\"font-size:14px;\">月成立至今，得到了广大客户与社会各界朋友的支持和认可，至此商富公司线上模式——商富贷正式运营，又有这么多新老客户的关心和支持，本人非常感激；另一方面，我们战战兢兢，我们做的是互联网，一不小心就可能被唾液淹死，我们做的是金融，风险控制不好保证投资者的利益谈何容易；在此我们庄严承诺，一：我们不跑路；二：我们将竭尽全力把商富贷做成网贷行业的佼佼者。</span></span><span style=\"font-family:\'Calibri\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:16pt;\"><span style=\"font-size:14px;\">2013</span><span style=\"font-size:14px;\">年我们将在以下方面开展工作：一加强技术创新，让我们这个平台更好用，不断提高网站的透明度，安全度；二加强业务推广，加强风险控制，推优质标，保证大家的资金安全，提高网站的公信力，知名度，美誉度；三加强公司内部管理，加强人才培养，加强资源整合，提高公司的正能量；四加强和投资者的沟通，接受大家的建议，保证投资者的利益。</span></span><span style=\"font-family:\'Calibri\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14px;\">朋友们，新一年的到来，标志着商富公司线下运作已横跨三年，我们过去走过的道路已经沉淀着量的积累，我们未来的道路必将展示质的飞跃！</span><span style=\"font-family:\'Calibri\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14px;\">杭州是人间天堂，是休闲之都，是品质之城，邀请朋友到杭州来旅游，到我们公司来考察！</span><span style=\"font-family:\'Calibri\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:24pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:14px;\">杭州欢迎您！商富贷欢迎您！顺祝大家新年快乐，身体健康，财源广进！</span><span style=\"font-family:\'Calibri\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:72pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:16pt;\"><span style=\"font-size:14px;\">商富贷总裁兼总经理林泽标&nbsp;&nbsp;</span><span style=\"font-size:14px;\">2013</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\">1</span><span style=\"font-size:14px;\">月</span><span style=\"font-size:14px;\">4</span><span style=\"font-size:14px;\">日</span></span><span style=\"font-family:\'宋体\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:16pt;\"></span>\r\n	</p>\r\n	<p style=\"text-indent:32pt;\">\r\n		<span style=\"font-family:\'宋体\';font-size:16pt;\"><span style=\"font-size:14px;\">林泽标简历：</span><span style=\"font-size:14px;\">1983</span><span style=\"font-size:14px;\">年</span><span style=\"font-size:14px;\">6</span><span style=\"font-size:14px;\">月出生，浙江大学</span><span style=\"font-size:14px;\">MBA</span><span style=\"font-size:14px;\">毕业，先后在工商银行，泰隆银行任职，擅长小额信贷风险管理。</span></span><span style=\"font-family:\'Calibri\';font-size:16pt;\"></span>\r\n	</p>\r\n</div>', '10', '0', '0', null, '1', '1357789028', '60.186.235.156');
