INSERT INTO `dw_article` VALUES ('118', '126', '商富贷积极回应投资者建议', null, '1', null, null, '', '2013-01-18 16:15:53', null, '', null, '', null, null, null, '<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">商富贷</span><span style=\"font-family:\'宋体\';font-size:14px;\">网贷平台</span><span style=\"font-family:\'宋体\';font-size:14px;\">上线以来，得到了各位投资者的关心和支持，大家给也我们一些批评，建言，建议，方案，吐槽，讽刺，奚落，痛骂，在此我们表示衷心的感谢！“良药苦口利于病，忠言逆耳利于行”，我们现在摘取大家的一些好的建议，并提出我们的改进措施，与诸君共享。</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'Calibri\';font-size:14px;\"><strong>pts123</strong></span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">你们网站公告有问题&nbsp;改下吧</span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>商富贷</strong></span><span style=\"font-family:\'宋体\';font-size:14px;\"><strong>：</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">我们公告修改过来了，还有错别字，用词不严谨，实在让人汗颜。</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'宋体\';font-size:14px;\"><strong>我&nbsp;&nbsp;</strong></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">不和别的平台争利率奖励高低，就要把客户体验做上去，不能两样都输给人家，那就完了</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">光说没用，现在大家公认四达和一起好最透明，去看看人家，学学，最重要的是执行力，行动迅速，用事实说话，改进</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">要做到透明，各种数据及时公布，包括逾期，这样大资金才敢进来。你们不虚心改进的话，做起来很难。现在半死不活的平台很多</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>商富贷</strong></span><span style=\"font-family:\'宋体\';font-size:14px;\"><strong>：</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">我们的目标就是把我们的网站做到像玻璃般透明，像雪花般剔透，和投资者零距离。并且加强技术更新，提高投资者投资体验满意度；目前正在改进标的透明度问题，因为涉及到系统程序问题，请给予我们时间。</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><strong><span style=\"font-size:14px;\">国付宝</span></strong><span style=\"font-size:14px;\"><strong>--</strong></span><span style=\"font-size:14px;\"><strong>李&nbsp;</strong></span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"font-size:14px;\">@</span><span style=\"font-size:14px;\">信贷部</span><span style=\"font-size:14px;\">-</span><span style=\"font-size:14px;\">王总&nbsp;把群升级</span><span style=\"font-size:14px;\">1000</span><span style=\"font-size:14px;\">人吧</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>商富贷：</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"font-size:14px;\">已经升级到</span><span style=\"font-size:14px;\">1000</span><span style=\"font-size:14px;\">人了。</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'宋体\';font-size:14px;\"><strong>我</strong></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">你们最好把活动期间秒的时间，限额，利率统一规划好，在公告中，讲清楚。反正是待收秒。这样投资人就会投月标的。你是新平台，以前公告中也有忽悠的地方，大家不怎么信任你们，怕你们到时又变。别人投资了，你又变了，说没秒了就不好。</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>商富贷：</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">目前我们提前公布未来三天的秒标情况，并做好秒标的规划。</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'宋体\';font-size:14px;\"><strong>我</strong></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">每天投资排名的礼品的具体情况，什么东西，有几种。要透明嘛。还说是搞网贷的&nbsp;</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'宋体\';font-size:14px;\"><strong>我&nbsp;&nbsp;</strong></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">别人不知道具体情况，收到自己不喜欢的，会很不爽</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">商富贷</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"font-size:14px;\">：目前我们送的是临安的山核桃</span><span style=\"font-size:14px;\">2</span><span style=\"font-size:14px;\">斤，杏仁</span><span style=\"font-size:14px;\">1</span><span style=\"font-size:14px;\">斤，价值</span><span style=\"font-size:14px;\">180</span><span style=\"font-size:14px;\">元，请看我们每天的投资者前三名信息并与我们客服联系，方便邮寄。</span></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'Calibri\';font-size:14px;\"><strong>SNAKE</strong></span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">你们的论坛为什么都是英文能解释下么&nbsp;</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'Calibri\';font-size:14px;\"><strong>SNAKE&nbsp;</strong></span><span style=\"font-family:\'Calibri\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">是你们认为自己水平比较好呢····还是我们水平比较低？&nbsp;</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"><span style=\"font-size:14px;\">商富贷</span><span style=\"font-size:14px;\">-</span><span style=\"font-size:14px;\">旭阳回复：这个是我们论坛建设公司搭建的时候，导入了一个错误的数据，现在在测试调整，修改好后我们论坛会</span></span><span style=\"font-family:\'宋体\';font-size:14px;\">及时上线</span><span style=\"font-family:\'宋体\';font-size:14px;\">。</span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:10.5pt;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\"><strong>投资人：</strong></span><span style=\"font-family:\'宋体\';font-size:10.5pt;\"><strong><span style=\"font-size:14px;\">★</span></strong><span style=\"font-size:14px;\"><strong>AWP</strong></span><span style=\"font-size:14px;\"><strong>★</stro