INSERT INTO `dw_article` VALUES ('230', '22', '商富贷2013年端午节放假通知 ', null, '1', null, null, '商富贷', '2013-06-06 09:16:10', null, '商富贷', null, '', null, null, null, '<span style=\"white-space:nowrap;font-size:14px;\">尊敬的商富贷会员： </span><br />\r\n<br />\r\n<span style=\"white-space:nowrap;font-size:14px;\"> 大家好！ </span><br />\r\n<br />\r\n<span style=\"white-space:nowrap;font-size:14px;\"> 根据《国务院办公厅关于2013年部分节假日安排的通知》，经研究决定，现将平台2013年端午节假期安排公布如下： </span><br />\r\n<br />\r\n<span style=\"white-space:nowrap;font-size:14px;\"> 1、6月8日（周六）、6月9日（周日）正常上班。（正常发标，处理提现及充值等业务。） </span><br />\r\n<p>\r\n	<span style=\"white-space:nowrap;font-size:14px;\"> </span><span style=\"white-space:nowrap;font-size:14px;\"> </span><span style=\"white-space:nowrap;font-size:14px;\">2、6月10日（周一）、6月11日（周二）、 6月12日（周三）公司放假三天。（不处理提现等业务，请投资者自行安排好时间。） </span> \r\n</p>\r\n<span style=\"font-size:14px;\"> </span><span style=\"white-space:nowrap;font-size:14px;\"> 3、放假期间如有问题，可咨询在线客服，商富贷客服人员值班时间如下： </span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><span style=\"white-space:nowrap;font-size:14px;\"> 6月10日（周一）、6月11日（周二）、6月12日（周三）安排客服值班，有什么问题可以咨询当天值班客服。值班时间：10:00 —— 15:00</span><span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<span style=\"white-space:nowrap;font-size:14px;\"> 4、6月13日（周四）正常上班。 </span><br />\r\n<br />\r\n<span style=\"white-space:nowrap;font-size:14px;\"> 放假期间给您带来的诸多不便之处，敬请谅解；感谢您对商富贷的支持与理解！</span><br />\r\n<span style=\"white-space:nowrap;font-size:14px;\"> </span><br />\r\n<span style=\"white-space:nowrap;\"><br />\r\n</span><br />\r\n<div style=\"text-align:right;\">\r\n	<span style=\"white-space:nowrap;font-size:14px;\"> </span><span style=\"white-space:nowrap;font-size:14px;\">商富贷运营中心 </span> \r\n</div>\r\n<span style=\"white-space:nowrap;\"> \r\n<div style=\"text-align:right;\">\r\n	<span style=\"font-size:14px;\"></span><span style=\"font-size:14px;\"> 2013.06.05</span> \r\n</div>\r\n</span> \r\n<div style=\"white-space:nowrap;\">\r\n	<br />\r\n</div>', '10', '0', '0', null, '1', '1370589774', '115.199.142.37');
