INSERT INTO `dw_article` VALUES ('269', '22', '9月7日商富贷线上投资者交流会公告', null, '1', '', null, '商富贷', '2013-08-22 11:35:09', null, '', null, '', null, null, null, '<!--StartFragment -->\r\n<div>\r\n	尊敬的商富贷客户朋友们：&nbsp;&nbsp;&nbsp;&nbsp;\r\n</div>\r\n<div>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;商富贷准备9月7日上午9点半在杭州东方豪生酒店（杭州艮山西路288号)举办投资者交流会，希望大家踊跃报名。报名请联系我们的客服和网络理财人员。客服&nbsp;瑶瑶、倩倩、水晶、客服小彭、信贷部王总。\r\n</div>\r\n<div>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp; 联系电话：0571-87984169&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</div>\r\n<div>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;商富官方群：81393641\r\n</div>', '10', '0', '0', null, '1', '1377143098', '115.206.104.252');
INSERT INTO `dw_article` VALUES ('270', '22', '商富贷开通自动投标功能，欢迎大家踊跃投标', null, '1', '', null, '商富贷', '2013-08-22 14:39:23', null, '商富贷', null, '', null, null, null, '<div>\r\n	尊敬的商富贷客户朋友们：&nbsp;&nbsp;&nbsp;&nbsp;\r\n</div>\r\n<div>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;由于近期商富贷平台很火，广大投资者投资很踊跃，那些没有第一时间守候在电脑前的用户没有抢到标，针对客户的这个问题，商富贷认真研究之后决定开通自动投标功能（QQ群81393641共享有自动投标指南），让那些没有充裕时间上网的人也能抢到标，真正的为客户着想，谢谢客户们一直以来的支持！\r\n</div>', '10', '0', '0', null, '1', '1377156547', '115.206.104.252');
