INSERT INTO `dw_article` VALUES ('275', '22', '商富贷线上投资者交流会流程', null, '1', '', null, '商富贷', '2013-08-29 16:38:28', null, '商富贷', null, '', null, null, null, '<p>\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 商富贷</span><span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\">线上投资者交流会流程</span><span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\"></span> \r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;font-weight:bold;\"></span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">时间：</span><span style=\"font-family:\'宋体\';font-size:14px;\">2013</span><span style=\"font-family:\'宋体\';font-size:14px;\">年</span><span style=\"font-family:\'宋体\';font-size:14px;\">9</span><span style=\"font-family:\'宋体\';font-size:14px;\">月</span><span style=\"font-family:\'宋体\';font-size:14px;\">7</span><span style=\"font-family:\'宋体\';font-size:14px;\">日日</span><span style=\"font-family:\'宋体\';font-size:14px;\">9:30--17:00</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">地点：</span><span style=\"font-family:\'宋体\';font-size:14px;\">杭州东方豪生酒店（杭州艮山西路288号)三楼</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">联系QQ：信贷部王总</span><span style=\"font-family:\'宋体\';font-size:14px;\">QQ295322275</span><span style=\"font-family:\'宋体\';font-size:14px;\">、</span><span style=\"font-family:\'宋体\';font-size:14px;\">客服瑶瑶</span><span style=\"font-family:\'宋体\';font-size:14px;\">QQ1502765320</span><span style=\"font-family:\'宋体\';font-size:14px;\">、客服小彭&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">QQ997589069</span><span style=\"font-family:\'宋体\';font-size:14px;\">、</span><span style=\"font-family:\'宋体\';font-size:14px;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">客服娜娜</span><span style=\"font-family:\'宋体\';font-size:14px;\">QQ61932113</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">联系电话：</span><span style=\"font-family:\'宋体\';font-size:14px;\">0571-87984169.</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">联系手机：王总</span><span style=\"font-family:\'宋体\';font-size:14px;\">18806513937</span><span style=\"font-family:\'宋体\';font-size:14px;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">陆</span><span style=\"font-family:\'宋体\';font-size:14px;\">学</span><span style=\"font-family:\'宋体\';font-size:14px;\">云</span><span style=\"font-family:\'宋体\';font-size:14px;\">15888810841&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">彭庆庆&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">13634184450</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">一、会议流程及时间安排</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">1</span><span style=\"font-family:\'宋体\';font-size:14px;\">、&nbsp;签到</span><span style=\"font-family:\'宋体\';font-size:14px;\">(8</span><span style=\"font-family:\'宋体\';font-size:14px;\">：</span><span style=\"font-family:\'宋体\';font-size:14px;\">30--9</span><span style=\"font-family:\'宋体\';font-size:14px;\">：</span><span style=\"font-family:\'宋体\';font-size:14px;\">30</span><span style=\"font-family:\'宋体\';font-size:14px;\">）</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">2</span><span style=\"font-family:\'宋体\';font-size:14px;\">、&nbsp;主持献词（</span><span style=\"font-family:\'宋体\';font-size:14px;\">9:30-9:35</span><span style=\"font-family:\'宋体\';font-size:14px;\">）&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">3</span><span style=\"font-family:\'宋体\';font-size:14px;\">、&nbsp;林总致词（</span><span style=\"font-family:\'宋体\';font-size:14px;\">9</span><span style=\"font-family:\'宋体\';font-size:14px;\">：</span><span style=\"font-family:\'宋体\';font-size:14px;\">35--9:50</span><span style=\"font-family:\'宋体\';font-size:14px;\">）</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">4</span><span style=\"font-family:\'宋体\';font-size:14px;\">、&nbsp;王召富投资者交流（</span><span style=\"font-family:\'宋体\';font-size:14px;\">9</span><span style=\"font-family:\'宋体\';font-size:14px;\">：</span><span style=\"font-family:\'宋体\';font-size:14px;\">50:1200</span><span style=\"font-family:\'宋体\';font-size:14px;\">）</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:23.25pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">商富公司介绍</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:23.25pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">商富公司贷款产品介绍</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:23.25pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">商富公司风险体系介绍</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:23.25pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">商富公司发展规划介绍</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">5、投资者监督委员会成立（</span><span style=\"font-family:\'宋体\';font-size:14px;\">12:00-12:20</span><span style=\"font-family:\'宋体\';font-size:14px;\">）&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:14px;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">6</span><span style=\"font-family:\'宋体\';font-size:14px;\">、进餐（</span><span style=\"font-family:\'宋体\';font-size:14px;\">12:30--14:30</span><span style=\"font-family:\'宋体\';font-size:14px;\">）</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">7、公司考察（</span><span style=\"font-family:\'宋体\';font-size:14px;\">14:30--15:30</span><span style=\"font-family:\'宋体\';font-size:14px;\">）</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">8、二手车公司考察（</span><span style=\"font-family:\'宋体\';font-size:14px;\">15:30--16:30</span><span style=\"font-family:\'宋体\';font-size:14px;\">）</span><span style=\"font-family:\'Times New Roman\';font-size:12pt;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">9、停车场考察（</span><span style=\"font-family:\'宋体\';font-size:14px;\">16:30-17:00</span><span style=\"font-family:\'宋体\';font-size:14px;\">）</span><span style=\"font-family:\'宋体\';font-size:12pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:21pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:14px;\">如有变更和调整会另行通知。需要到车站接送的投资者请提前联系相关人员。感谢广大投资者对商富贷的支持！</span><span style=\"font-family:\'宋体\';font-size:16pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:16pt;\"></span> \r\n</p>\r\n<p style=\"text-indent:32pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;\"></span><span style=\"font-family:\'宋体\';font-size:16pt;\"></span>\r\n</p>\r\n<p style=\"text-indent:88pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:16pt;\">&nbsp;</span><span style=\"font-family:\'宋体\';font-size:16pt;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size:18px;\">&nbsp;</span></span><span style=\"font-family:\'宋体\';font-size:18px;\">商富贷&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';\"></span> \r\n</p>\r\n<p style=\"text-indent:88pt;\">\r\n	<span style=\"font-family:\'宋体\';font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:18px;\">&nbsp;</span><span style=\"font-family:\'宋体\';\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:18px;\">&nbsp;</span></span><span style=\"font-family:\'宋体\';font-size:18px;\">2013</span><span style=\"font-family:\'宋体\';font-size:18px;\">年</span><span style=\"font-family:\'宋体\';font-size:18px;\">8</span><span style=\"font-family:\'宋体\';font-size:18px;\">月</span><span style=\"font-family:\'宋体\';font-size:18px;\">29</span><span style=\"font-family:\'宋体\';font-size:18px;\"><span style=\"font-size:18px;\">日&nbsp;</span></span><span style=\"font-family:\'宋体\';font-size:18px;\">&nbsp;&nbsp;</span><span style=\"font-family:\'宋体\';font-size:16pt;\"></span> \r\n</p>', '10', '0', '0', null, '1', '1377765904', '218.72.45.40');
