INSERT INTO `dw_article` VALUES ('284', '22', '商富贷秋季线上客户交流会顺利召开', null, '1', '', null, '商富贷', '2013-09-10 15:00:44', null, '商富贷', null, '', null, null, null, '<p style=\"MARGIN-TOP: 0px; MARGIN-BOTTOM: 0px\"><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">&nbsp;&nbsp;&nbsp; </span></p><p style=\"MARGIN-TOP: 0px; MARGIN-BOTTOM: 0px\"><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">&nbsp;&nbsp;&nbsp; 2013<span style=\"FONT-FAMILY: 宋体\">年</span><span style=\"FONT-FAMILY: Times New Roman\">9</span><span style=\"FONT-FAMILY: 宋体\">月</span><span style=\"FONT-FAMILY: Times New Roman\">7</span><span style=\"FONT-FAMILY: 宋体\">日上午</span><span style=\"FONT-FAMILY: Times New Roman\">10</span><span style=\"FONT-FAMILY: 宋体\">点，商富贷秋季投资者交流会在东方豪生大酒店如期举行。为了投资者更好地了解商富贷的经营理念、投资风险及收益，公司特意召集了各位线上的投资者到场交流讨论。</span></span></p><p style=\"MARGIN-TOP: 0px; MARGIN-BOTTOM: 0px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style=\"TEXT-ALIGN: center\"><img style=\"WIDTH: 604px; HEIGHT: 392px\" title=\"mmexport1378774887909.jpg\" src=\"/data/upfiles/20130910/91381378797367473.jpg\" width=\"640\" height=\"424\"/></p><p>&nbsp;</p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\"><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">&nbsp;交流会的开幕</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">，董事长林泽标首先就商富贷汽车综合金融发展产业链的观点发言。阐述了商富贷在近五年来的发展规划，立志要将汽车抵押小额贷款做大、做强、做规范，让投资者放心，借贷者安心，公司稳步发展，实现三方共赢的局面。</span></p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\">&nbsp;</p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <img style=\"WIDTH: 623px; HEIGHT: 369px\" title=\"mmexport1378774881989.jpg\" src=\"/data/upfiles/20130910/3481378797584140.jpg\" width=\"639\" height=\"369\"/></p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\">&nbsp;</p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\"><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">会上</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">，商富贷</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">运营</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">总监王召富</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">以</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">打造安全稳健的投资平台</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">为主题</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">发表讲话</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">，并对商富公司的组织构架、发展理念、发展目标、公司产品等做了系统介绍，对于</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">P2P<span style=\"FONT-FAMILY: 宋体\">平台</span></span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">投资者最关心的问题，钱在平台</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">上</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">是否安全，是否能够达到自己想要的收益？对此</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">王总重点讲解了商富贷是如何做好风险防范的，用生动的例子讲解了商富贷风控措施的每一步，处处体现了公司“</span><span style=\"FONT-FAMILY: &#39;Microsoft YaHei&#39;; FONT-SIZE: 16px\">诚信天下，客户为先</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">”的服务宗旨，</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">王</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">总风趣幽默的讲解得到了大家一阵一阵的掌声。</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">之后，</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">投资者纷纷提问，其中一个地道的老杭州人，直截了当地提到：“我就担心一个问题，万一你们老总跑路了怎么办？”大家一阵哄笑后，都严肃地等待着答案。王总机智幽默地回答：“我们老总喜爱散步，不喜欢跑路！”又引起了哄堂大笑。此时，林董事长上台，严肃认真地阐明了</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">公司</span><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">的立场：“公司的经营模式，导致我绝不会跑路……”铿锵有力的回答，给了投资者很大的信心。会后大家一起用餐，在轻松自在的环境中沟通、畅谈，结交各界人士。</span></p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\">&nbsp;</p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\"><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">“商富”人常怀感激之心，正是因为有了社会各界朋友的支持和关爱，“商富”才有了今天的成就和自信，前进的步履才因此更加坚实和有力。我们渴望合作和共赢，我们愿和所有朋友携手起来，团结奋进，共创未来！！</span></p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\">&nbsp;</p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\">&nbsp;</p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\">&nbsp;</p><p style=\"MARGIN-TOP: 0px; TEXT-INDENT: 32px; MARGIN-BOTTOM: 0px\"><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;商富贷</span></p><p style=\"MARGIN-TOP: 0px; MARGIN-BOTTOM: 0px\"><span style=\"FONT-FAMILY: &#39;宋体&#39;; FONT-SIZE: 16px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2013.9.9</span></p><p>&nbsp;</p>', '10', '0', '0', null, '1', '1378802731', '115.206.99.239');
