INSERT INTO `dw_site` VALUES ('22', 'article', '最新公告', 'gonggao', '1', '', '', '', '', '10', '1', '0', '', '<br />\r\n<p>\r\n	<span style=\"font-size:14px;\">通过平台的推荐，您可以将手中的富余资金出借给信用良好但缺少资金的大学生、工薪阶层、微小企业主、农民，帮助他们实现教育培训、电脑或家电购买、装修、兼职创业、脱贫致富等理想，同时还能够通过利息收入为您带来较高的稳定收益，实现精神和物质的双重回报。根据平台的历史经验数据，商富宝的年收益始终保持在12.5%以上。</span>\r\n</p>\r\n<br />\r\n<br />', 'index_[page].html', '[id].html', '[nid]', '0', '[code].html', 'about.html', 'about_content.html', '', '', '“商富宝”是商富公司为您量身打造的P2P信贷理财模式。\r\n', '0', '1284913454', '127.0.0.1');
