INSERT INTO `dw_site` VALUES ('57', 'article', '网站资费', 'feiyong', '1', '', '', '', '', '7', '1', '1', '', '<br />\r\n<br />\r\n<br />\r\n<br />\r\n<p style=\"background-color:white;\" align=\"left\">\r\n	<b><span style=\"font-family:宋体;color:red;font-size:14px;\">投资人费用 </span></b><b><span style=\"font-family:宋体;color:red;\"></span></b>\r\n</p>\r\n<p style=\"background-color:white;\" align=\"left\">\r\n	<b><span style=\"font-family:宋体;color:red;font-size:12pt;\"><br />\r\n</span></b>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;font-size:14px;\">1</span><span style=\"font-family:宋体;font-size:14px;\">、充值费用</span> \r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span><br />\r\n</span><span style=\"font-size:14px;\">投资者线上充值手续费全免，充多少到账多少；线下充值最低</span><span style=\"font-size:14px;\">1</span><span style=\"font-size:14px;\">万元起，充值即送充值金额的</span><span style=\"font-size:14px;\">0.5‰</span><span style=\"font-size:14px;\">的红包，该红包可以用来抵扣</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">提现费，但不能投标和提现。</span><span></span></span> \r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;font-size:14px;\">2</span><span style=\"font-family:宋体;font-size:14px;\">、提现费用（由第三方平台收取）</span> \r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span><br />\r\n</span><span style=\"font-size:14px;\">当投资者要求提现，将借款资金转至指定银行账户时，会发生转账费用，第三方支付平台将按以下标准收取相关费用，用户自充值之日起于</span><span style=\"font-size:14px;\">15</span><span style=\"font-size:14px;\">日之内提现的将收取金额的</span><span style=\"font-size:14px;\">3‰</span><span style=\"font-size:14px;\">为手续费。用户自充值之日起于</span><span style=\"font-size:14px;\">15</span><span style=\"font-size:14px;\">日之外提现的将维持以下规则：</span><span> </span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">金额</span><span style=\"font-size:14px;\"> 3</span><span style=\"font-size:14px;\">万（含）以下</span><span style=\"font-size:14px;\"> 3</span><span style=\"font-size:14px;\">万</span><span style=\"font-size:14px;\">--5</span><span style=\"font-size:14px;\">万（含）</span><span> </span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">收费</span><span style=\"font-size:14px;\"> 3</span><span style=\"font-size:14px;\">元</span><span style=\"font-size:14px;\">/</span><span style=\"font-size:14px;\">笔</span><span style=\"font-size:14px;\"> 5</span><span style=\"font-size:14px;\">元</span><span style=\"font-size:14px;\">/ </span><span style=\"font-size:14px;\">笔</span><span> </span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"> \r\n	<p>\r\n		<span style=\"font-size:14px;\">单笔提现金额在</span><span style=\"font-size:14px;\">1500</span><span style=\"font-size:14px;\">元（包含）以下，将不按照（</span><span style=\"font-size:14px;\">15</span><span style=\"font-size:14px;\">日内、外）提现规则收取费用，此类提现一律按照每笔金额的千分之三为手续费收取。 </span><span></span>\r\n	</p>\r\n<span></span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;font-size:14px;\">3</span><span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">、投资管理费</span><span><br />\r\n</span><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">收取投资者所得利息的</span><span style=\"font-size:14px;\">10%</span><span style=\"font-size:14px;\">作为管理费。</span><span style=\"font-size:14px;\">(</span><span style=\"font-size:14px;\">投资人所得利息由投资人自行缴纳所得税）</span><span> </span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;font-size:14px;\">4</span><span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">、 普通会员在充值交纳</span><span style=\"font-size:14px;\">150</span><span style=\"font-size:14px;\">元</span><span style=\"font-size:14px;\">/</span><span style=\"font-size:14px;\">年的会员费之后，经审核成功后即可成为</span><span style=\"font-size:14px;\">VIP</span><span style=\"font-size:14px;\">会员；超过</span><span style=\"font-size:14px;\">15</span><span style=\"font-size:14px;\">天以上借入者未还款，该笔借款将按坏帐处理，</span><span style=\"font-size:14px;\">VIP</span><span style=\"font-size:14px;\">会员可以获得由网站垫付的全额借款本金和利息，非</span><span style=\"font-size:14px;\">VIP</span><span style=\"font-size:14px;\">会员可以获得由网站垫付本金的</span><span style=\"font-size:14px;\">50%</span><span style=\"font-size:14px;\">。 </span><span></span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;font-size:14px;\"></span>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;color:red;font-size:12pt;\"><br />\r\n</span></b>\r\n</p>\r\n<p>\r\n	<b><span style=\"font-family:宋体;color:red;font-size:12pt;\"><span style=\"font-size:14px;\">借款人费用 </span><span></span></span></b>\r\n</p>\r\n<p>\r\n	<span><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">1</span><span style=\"font-size:14px;\">、借款费用</span><span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">商富贷收取的服务费将全部用于商富贷的本金保障计划，每月向借款人收取其借款本金的</span><span style=\"font-size:14px;\">0.5%</span><span style=\"font-size:14px;\">作为借款管理费（秒还标不收取管理费；净值标收取金额的</span><span style=\"font-size:14px;\">0.1%</span><span style=\"font-size:14px;\">）。天标请见商富贷天标说明：<a href=\"http://www.sfdai.com/article/detail.html?catalog=11&amp;id=81\">http://www.sfdai.com/article/detail.html?catalog=11&amp;id=81</a></span><span></span> \r\n</p>\r\n<p>\r\n	<span><br />\r\n</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">2</span><span style=\"font-size:14px;\">、充值费用</span><span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\">借款人充值手续费全免，充多少到账多少。 </span><span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span style=\"white-space:normal;font-size:14px;\">3</span><span style=\"white-space:normal;font-size:14px;\">、</span></span><span style=\"background-color:white;font-family:宋体;\"></span><span style=\"background-color:white;font-family:宋体;font-size:14px;\">提现费用（由第三方平台收取）</span> \r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"background-color:white;font-family:宋体;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"background-color:white;font-family:宋体;font-size:14px;\">当借款人要求提现，借款资金转至指定银行账户时，会发生转账费用，第三方支付平台将按以下标准收取相关费用。（净值标提现除外）</span> \r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><br />\r\n</span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">金额</span><span style=\"font-size:14px;\"> 3</span><span style=\"font-size:14px;\">万（含）以下</span><span style=\"font-size:14px;\"> 3</span><span style=\"font-size:14px;\">万</span><span style=\"font-size:14px;\">--5</span><span style=\"font-size:14px;\">万（含）</span><span> </span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">收费</span><span><span style=\"font-size:14px;\"> </span><span style=\"font-size:14px;\">3</span></span><span style=\"font-size:14px;\">元</span><span style=\"font-size:14px;\">/</span><span style=\"font-size:14px;\">笔</span><span><span style=\"font-size:14px;\"> </span><span style=\"font-size:14px;\">5</span></span><span style=\"font-size:14px;\">元</span><span style=\"font-size:14px;\">/ </span><span style=\"font-size:14px;\">笔</span><span><br />\r\n<br />\r\n</span></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<span style=\"font-family:宋体;\"><span style=\"font-size:14px;\">4</span><span style=\"font-size:14px;\">、 普通会员上传资料审核，并通过审核后即可申请</span><span style=\"font-size:14px;\">VIP</span><span style=\"font-size:14px;\">会员，</span><span style=\"font-size:14px;\">VIP</span><span style=\"font-size:14px;\">会员信息费</span><span style=\"font-size:14px;\">150</span><span style=\"font-size:14px;\">元</span><span style=\"font-size:14px;\">/</span><span style=\"font-size:14px;\">年，</span><span style=\"font-size:14px;\">VIP</span><span style=\"font-size:14px;\">会员费是在首笔成功借到款项内扣除。</span></span><SPAN style=\"FONT-FAMILY: \" color:#444444;?=\"\"><br />\r\n<br />\r\n</span><SPAN style=\"FONT-FAMILY: \" color:#444444;?=\"\"></span>\r\n</p>\r\n<p style=\"background:white;\">\r\n	<b><span style=\"font-family:宋体;color:red;font-size:14px;\">本页面内容仅供参考，以网站的公告和公司具体规定为准，最终解释权由商富贷所有。</span></b><span style=\"font-family:宋体;\"></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"></span>\r\n</p>\r\n<br />', 'index_[page].html', '[id].html', '[nid]', '0', '[code].html', 'about.html', 'about_content.html', '', '', '商富贷运营收费规则\r\n', '1', '1293536116', '221.175.53.209');
