INSERT INTO `dw_user_type` VALUES ('12', '黄金会员', '', '3', '1', '2', '1．总积分达到5000分（借款用户全额还清积分必须达到3500分）且没有迟还款和逾期还款记录的用户，可以申请成为白金会员，<br />\r\n2．成为金牌会员后，有迟还款或者逾期还款的用户没有资格申请为白金会员，且会员等级由金牌会员降为银牌会员。<br />\r\n3．迟还款一次，信用额度降低5%；逾期超过3天，信用额度降低10%。<br />\r\n', '1．总积分达到5000分（借款用户全额还清积分必须达到3500分）且没有迟还款和逾期还款记录的用户，可以申请成为白金会员，<br />\r\n2．成为金牌会员后，有迟还款或者逾期还款的用户没有资格申请为白金会员，且会员等级由金牌会员降为银牌会员。<br />\r\n3．迟还款一次，信用额度降低5%；逾期超过3天，信用额度降低10%。<br />\r\n', '1326078049', '183.145.184.206');
